import React, { useState, useEffect } from 'react';
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
  ResponsiveContainer, PieChart, Pie, Cell
} from 'recharts';
import {
  Send, Users, Target, Mail, Plus, Trash2, Eye, DollarSign
} from 'lucide-react';

const Marketing = () => {
  const [campaigns, setCampaigns] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [selectedCampaign, setSelectedCampaign] = useState(null);
  const [retentionRecommendations, setRetentionRecommendations] = useState([]);
  const [campaignStats, setCampaignStats] = useState(null);

  const [newCampaign, setNewCampaign] = useState({
    name: '',
    type: 'email',
    targetAudience: 'all',
    message: '',
    discountPercentage: 0,
    startDate: '',
    endDate: '',
    targetRiders: ['rider001', 'rider002', 'rider003'] // âœ… mock data
  });

  useEffect(() => {
    fetchCampaigns();
    fetchRetentionRecommendations();
    fetchCampaignStats();
  }, []);

  // Automatically update targetRiders when selecting "high-risk"
  useEffect(() => {
    if (newCampaign.targetAudience === 'high-risk') {
      setNewCampaign((prev) => ({
        ...prev,
        targetRiders: retentionRecommendations.map(r => r.riderId)
      }));
    }
  }, [newCampaign.targetAudience, retentionRecommendations]);

  const fetchCampaigns = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/marketing/campaigns`);
      const data = await response.json();
      setCampaigns(data.campaigns || []);
    } catch (error) {
      console.error('Error fetching campaigns:', error);
    }
  };

  const fetchRetentionRecommendations = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_ML_SERVICE_URL}/recommendations/retention`);
      const data = await response.json();
      setRetentionRecommendations(data.recommendations || []);
    } catch (error) {
      console.error('Error fetching retention recommendations:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchCampaignStats = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/marketing/campaigns`);
      const data = await response.json();
      setCampaignStats(data.stats);
    } catch (error) {
      console.error('Error fetching campaign stats:', error);
    }
  };

  const handleCreateCampaign = async () => {
    try {
      if (!newCampaign.name || !newCampaign.message) {
        alert('Please fill in all required fields');
        return;
      }

      const response = await fetch(`${process.env.REACT_APP_API_URL}/marketing/campaigns`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newCampaign),
      });

      if (!response.ok) {
        const errorData = await response.text();
        throw new Error(`HTTP error! status: ${response.status}, message: ${errorData}`);
      }

      await response.json();
      setShowCreateModal(false);
      setNewCampaign({
        name: '',
        type: 'email',
        targetAudience: 'all',
        message: '',
        discountPercentage: 0,
        startDate: '',
        endDate: '',
        targetRiders: ['rider001', 'rider002', 'rider003']
      });
      fetchCampaigns();
    } catch (error) {
      console.error('Error creating campaign:', error);
      alert(`Failed to create campaign: ${error.message}`);
    }
  };

  const handleDeleteCampaign = async (campaignId) => {
    if (window.confirm('Are you sure you want to delete this campaign?')) {
      try {
        await fetch(`${process.env.REACT_APP_API_URL}/marketing/campaigns/${campaignId}`, {
          method: 'DELETE',
        });
        fetchCampaigns();
      } catch (error) {
        console.error('Error deleting campaign:', error);
      }
    }
  };

  const handleLaunchCampaign = async (campaignId) => {
    try {
      await fetch(`${process.env.REACT_APP_API_URL}/marketing/campaigns/${campaignId}/launch`, {
        method: 'POST',
      });
      fetchCampaigns();
    } catch (error) {
      console.error('Error launching campaign:', error);
    }
  };

  const sendRetentionOffer = async (riderId) => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/marketing/retention-offer`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ riderId }),
      });

      if (response.ok) alert('Retention offer sent successfully!');
    } catch (error) {
      console.error('Error sending retention offer:', error);
    }
  };

  const getCampaignStatusColor = (status) => {
    switch (status) {
      case 'active': return 'bg-green-100 text-green-800';
      case 'scheduled': return 'bg-blue-100 text-blue-800';
      case 'completed': return 'bg-gray-100 text-gray-800';
      case 'paused': return 'bg-yellow-100 text-yellow-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const COLORS = ['#4ECDC4', '#45B7D1', '#FFEAA7'];
  const campaignTypeData = campaignStats ? [
    { name: 'Email', value: campaignStats.email || 0 },
    { name: 'SMS', value: campaignStats.sms || 0 },
    { name: 'Push', value: campaignStats.push || 0 },
  ] : [];

  const StatCard = ({ title, value, icon: Icon, color, subtitle }) => (
    <div className="bg-white rounded-lg shadow-md p-6 border-l-4" style={{ borderLeftColor: color }}>
      <div className="flex items-center justify-between">
        <div>
          <p className="text-sm font-medium text-gray-600">{title}</p>
          <p className="text-2xl font-bold text-gray-900">{value}</p>
          {subtitle && <p className="text-sm text-gray-500">{subtitle}</p>}
        </div>
        <Icon className="h-8 w-8" style={{ color }} />
      </div>
    </div>
  );

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-6">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Marketing Dashboard</h1>
            <p className="text-gray-600">Manage campaigns and retention strategies</p>
          </div>
          <button
            onClick={() => setShowCreateModal(true)}
            className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            <Plus className="h-4 w-4 mr-2" />
            Create Campaign
          </button>
        </div>

        {/* Campaign Stats */}
        {campaignStats && (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <StatCard title="Active Campaigns" value={campaignStats.active || 0} icon={Target} color="#4ECDC4" />
            <StatCard title="Total Reach" value={(campaignStats.totalReach || 0).toLocaleString()} icon={Users} color="#45B7D1" />
            <StatCard title="Avg Open Rate" value={`${campaignStats.averageOpenRate || 0}%`} icon={Mail} color="#96CEB4" />
            <StatCard title="Revenue Generated" value={`$${(campaignStats.revenue || 0).toLocaleString()}`} icon={DollarSign} color="#FFEAA7" />
          </div>
        )}

        {/* Charts */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold mb-4">Campaign Types</h3>
            <ResponsiveContainer width="100%" height={300}>
              <PieChart>
                <Pie data={campaignTypeData} cx="50%" cy="50%" labelLine={false} outerRadius={80} fill="#8884d8" dataKey="value">
                  {campaignTypeData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
          </div>

          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold mb-4">Campaign Performance</h3>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={campaigns.slice(0, 5)}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="openRate" fill="#4ECDC4" name="Open Rate %" />
                <Bar dataKey="clickRate" fill="#45B7D1" name="Click Rate %" />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Create Campaign Modal */}
        {showCreateModal && (
          <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
            <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
              <div className="mt-3">
                <h3 className="text-lg font-medium text-gray-900 mb-4">Create New Campaign</h3>
                <div>
                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-2">Campaign Name</label>
                    <input
                      type="text"
                      value={newCampaign.name}
                      onChange={(e) => setNewCampaign({ ...newCampaign, name: e.target.value })}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md"
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-2">Campaign Type</label>
                    <select
                      value={newCampaign.type}
                      onChange={(e) => setNewCampaign({ ...newCampaign, type: e.target.value })}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md"
                    >
                      <option value="email">Email</option>
                      <option value="sms">SMS</option>
                      <option value="push">Push Notification</option>
                    </select>
                  </div>

                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-2">Target Audience</label>
                    <select
                      value={newCampaign.targetAudience}
                      onChange={(e) => setNewCampaign({ ...newCampaign, targetAudience: e.target.value })}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md"
                    >
                      <option value="all">All Riders</option>
                      <option value="active">Active Riders</option>
                      <option value="inactive">Inactive Riders</option>
                      <option value="high-risk">High Churn Risk</option>
                    </select>
                  </div>

                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-2">Message</label>
                    <textarea
                      value={newCampaign.message}
                      onChange={(e) => setNewCampaign({ ...newCampaign, message: e.target.value })}
                      rows={3}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md"
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-2">Discount Percentage</label>
                    <input
                      type="number"
                      value={newCampaign.discountPercentage}
                      onChange={(e) => setNewCampaign({ ...newCampaign, discountPercentage: Number(e.target.value) })}
                      min="0"
                      max="100"
                      className="w-full px-3 py-2 border border-gray-300 rounded-md"
                    />
                  </div>

                  <div className="flex justify-end space-x-3">
                    <button
                      onClick={() => setShowCreateModal(false)}
                      className="px-4 py-2 text-sm text-gray-700 bg-gray-200 rounded-md"
                    >
                      Cancel
                    </button>
                    <button
                      onClick={handleCreateCampaign}
                      className="px-4 py-2 text-sm text-white bg-blue-600 rounded-md"
                    >
                      Create Campaign
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Marketing;
